import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';



function Square(props) {
    return (<button className="square" onClick={() => props.onClick()}>
        {props.value}
    </button>)
}

class Board extends React.Component {
    renderSquare(i) {
        return <Square value={this.props.squares[i]} onClick={() => this.props.onClick(i)} />;
    }
    render() {
        let board = []
        for (let i = 0; i < 3; i++) {
            let row = []
            for (let j = 0; j < 3; j++) {
                row.push(this.renderSquare(i * 3 + j))
            }
            board.push(<div className="board-row">{row}</div>)
        }
        return(
            <div>
                {board}
            </div>
        )
    }
}

class Game extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            history: [{
                squares: Array(9).fill(null),
                posX: -1,
                posY: -1
            }],
            stepNumber: 0,
            xIsNext: true,
            toggleSort: true
        }
    }
    calculateWinner(squares) {
        const winLine = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6]
        ]
        for (let i = 0; i < winLine.length; i++) {
            const [a, b, c] = winLine[i];
            if (squares[a] && squares[a] == squares[b] && squares[a] == squares[c]) {
                return squares[a]
            }
        }
        return null
    }
    jumpTo(stepNumber) {
        this.setState({
            stepNumber: stepNumber,
            xIsNext: (stepNumber % 2) === 0
        })
    }
    handleClick(i) {
        const history = this.state.history.slice(0, this.state.stepNumber + 1)
        const current = history[history.length - 1]
        const squares = current.squares.slice()
        if (this.calculateWinner(squares) || squares[i]) {
            return
        }
        squares[i] = this.state.xIsNext ? 'X' : 'O'
        this.setState({
            history: history.concat([{
                squares: squares,
                posX: i % 3,
                posY: Math.floor(i / 3),
            }]),
            stepNumber: history.length,
            xIsNext: !this.state.xIsNext
        })
    }
    render() {
        const history = this.state.history
        const current = history[this.state.stepNumber]
        const winner = this.calculateWinner(current.squares)

        const bold = {
            fontWeight: 'bold'
        }

        const normal = {
            fontWeight: 'normal'
        }

        const moves = history.map((step, move) => {
            const posX = this.state.history[move].posX;
            const posY = this.state.history[move].posY;
            const desc = move ? 'Go to move #' + move + `(${posX}, ${posY})` : 'Go to game start';
            return (
                <li key={move}>
                    <button onClick={() => this.jumpTo(move)} style={this.state.stepNumber === move ? bold : normal}>{desc}</button>
                </li>
            )
        })
        let status;
        if (winner) {
            status = 'The winner is: ' + winner;
        }
        else {
            status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O')
        }
        return (
            <div className="game">
                <div className="game-board">
                    <Board squares={current.squares} onClick={(i) => this.handleClick(i)} />
                </div>
                <div className="game-info">
                    <div>{status}</div>
                    <button onClick={this.sortMoves()}>Sort</button>
                    <ol>{moves}</ol>
                </div>
            </div>
        );
    }
    sortMoves() {
        history = this.state.history.slice()
        let count = 0;
        while (count < history.length / 2) {
            const tmp = history[count]
            history[count] = history[history.length - count]
        }
    }
}

ReactDOM.render(
    <Game />,
    document.getElementById('root')
);
